import React from "react";
import PageHeaderContent from "../../components/pageHeaderContents";
import { Animate } from "react-simple-animate";
import './style.scss';

const About = () => {

    const personalDetails = [
        {
            label: "Name: ",
            value: "shivani kumari"
        },
        {
            label: "email: ",
            value: "shivanik475@gmail.com"
        },
        {
            label: "contact: ",
            value: "9953618958"
        },
        {
            label: "linkedin: ",
            value: ""
        },
        {
            label: "github: ",
            value: ""
        }
    ];

    const jobSummary = 'I’m Shivani Kumari, an SDET transitioning to frontend development, with a strong background in software testing, I bring attention to detail and a focus on quality. I’m passionate about building responsive, user-friendly web apps and excited to apply my skills in React, JavaScript, and frontend technologies to create great user experiences.';


    return (
        <section id="about" className="about">
            <PageHeaderContent headerText="About Me" ></PageHeaderContent>
            <div className="about__content">
                <div className="about__content__personalWrapper">
                    <Animate
                        play
                        duration={1.5}
                        delay={0.5}
                        start={{ opacity: 0, color: "#fff" }}
                        end={{ opacity: 1, color: "#000" }}
                    >
                        <p>{jobSummary}</p>
                    </Animate>
                    <Animate
                        play
                        duration={1.5}
                        delay={0.5}
                        start={{ transform: "translateX(100vw)" }}
                        end={{ transform: "translateX(10px)" }}
                    >
                        <h3 className="personalInformationText">Personal Information</h3>
                        <ul>
                            {
                                personalDetails.map((item, i) => (
                                    <li key={i}>
                                        <span className="title">{item.label}</span>
                                        <span className="title">{item.value}</span>
                                    </li>
                                ))
                            }
                        </ul>
                    </Animate>
                </div>
            </div>
        </section>
    );
}

export default About;
