import React from "react";
import {BsInfoCircleFill} from 'react-icons/bs'
import PageHeaderContent from "../../components/pageHeaderContents";
import { Animate } from "react-simple-animate";
import './style.scss';

const About = () => {

    const personalDetails=[
        {
            label:"Name: ",
            value:"shivani kumari"
        },
        {
            label:"email: ",
            value:"shivanik475@gmail.com"
        },
        {
            label:"contact: ",
            value:"9953618958"
        },
        {
            label:"linkedin: ",
            value:""
        },
        {
            label: "github: ",
            value:""
        }
    ];

    const jobSummary='';


    return (
       <section id="about" className="about">
        <PageHeaderContent headerText="About Me" ></PageHeaderContent>
        <div className="about__content">
            <div className="about__content__personalWrapper">
                <p>{jobSummary}</p>
            </div>
            <Animate
                play 
                duration={1.5}
                delay={0.5}
                start={{ opacity: 0, transform: "translateX(100px)" }}
                end={{ opacity: 1, transform: "translateX(0px)" }}
            >
                <h3 className="personalInformationText">Personal Information</h3>
                <ul>
                    {
                        personalDetails.map((item, i) => (
                            <li key={i}>
                                <span className="title">{item.label}</span>
                                <span className="title">{item.value}</span>
                            </li>
                        ))
                    }
                </ul>
            </Animate>
        </div>
       </section>
    );
}

export default About;
